// START CLOCK SCRIPT

Number.prototype.pad = function (n) {
    for (var r = this.toString(); r.length < n; r = 0 + r) ;
    return r;
};

let lastTimeCorrected = new Date();

function updateClock(wrapperQ = "#client-timedate", correctionMs = 0) {
    const secondsToColor = (secs) => `hsl(${(12 * secs) % 360}, 80%, 50%)`;

    const clockWrapper = document.querySelector(wrapperQ);
    const now = (correctionMs === 0) ? new Date() : new Date(new Date().valueOf() + correctionMs);
    if (correctionMs
        && lastTimeCorrected.getSeconds() !== now.getSeconds()) {
        beep();
        clockWrapper.style.backgroundColor = secondsToColor(now.getSeconds());
        lastTimeCorrected = now;
    }
    const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    const clockValues = {
        "mon": months[now.getMonth()],
        "d": now.getDate(),
        "y": now.getFullYear(),
        "h": now.getHours().pad(2),
        "m": now.getMinutes().pad(2),
        "s": now.getSeconds().pad(2),
        "mi": now.getMilliseconds()
    };
    Object.keys(clockValues).forEach(name => {
        clockWrapper.querySelector(`.${name}`).firstChild.nodeValue = clockValues[name];
    });
}

function initClock(intervals = 1) {
    updateClock();
    window.setInterval(() => updateClock("#client-timedate"), intervals);
    window.setInterval(() => updateClock("#corrected-timedate", currentTimeCorrection), intervals);
}

const snd = new Audio("data:audio/wav;base64,");

function beep() {
    // snd.fastSeek();
    snd.play();
}

document.addEventListener("DOMContentLoaded", () => {
    initClock(1);
});
// END CLOCK SCRIPT


let currentTimeCorrection = 0;

function socketIoInit() {
    let socket = io();
    ntp.init(socket);
    socket.on("ntp:server_sync", () => {
        setTimeout(() => {
            currentTimeCorrection = -ntp.offset();
            // scheduleMusicPlayTest(currentTimeCorrection);
            document.querySelector("#clk-correction").innerHTML = `(${currentTimeCorrection})`;
            console.log("ntp sync: ", currentTimeCorrection);
        }, 30)
    })
}

setTimeout(socketIoInit, 1000);

let schedules = 0;

let scheduleTimeout;

function scheduleMusicPlayTest(correctionMs) {
    if (scheduleTimeout) clearTimeout(scheduleTimeout)
    schedules++;
    const now = new Date();
    const musicEl = document.querySelector("#test-song");
    musicEl.play();
    musicEl.currentTime = 10 * schedules;
    musicEl.pause();
    const halfMinInMs = (now.valueOf() + correctionMs) % (30 * 1000);
    const msToNextMinute = 30 * 1000 - halfMinInMs; //((58 - now.getSeconds()) * 1000) + (1000 - now.getMilliseconds()) + correctionMs;
    console.log("Scheduling music to " + msToNextMinute);
    document.querySelector("#sc-msg").innerHTML = `Scheduling music to: ${msToNextMinute}ms (now: ${now.getSeconds() + correctionMs / 1000}s)`;

    scheduleTimeout = setTimeout(() => {
        musicEl.play();
        setTimeout(() => {
            musicEl.pause();
            scheduleMusicPlayTest(correctionMs);
        }, 5 * 1000);
    }, msToNextMinute)
}
